custom_css:
body {
  font-family: "undefined";
  font-weight: normal;
}

/* 基础消息气泡 */
#chat .mes_block {
  border-radius: 20px !important;
  box-sizing: border-box;
  background-color: #fff !important;
}

/* 用户名称 */
#chat .name_text {
  color: #2782D7 !important;
  font-weight: bold !important;
  font-size: 14px !important;
}

#chat .mes {
  padding: 10px;
  border-radius: 10px;
  background-color: var(--SmartThemeBotMesBlurTintColor);
  margin-bottom: 5px;
  border: none !important;
}
/* 发送消息框 */
#send_textarea{
  clip-path: none !important;
}

/* 信息文本 */
.mes_text em,
.mes_text i {
  color: #4D4D4D !important
}
.tags>.tag_name{
  color: #f1f1f1 !important;
}
.tag>.tag_name{
  color: #4D4D4D;
}
/* 代码框 */
pre {
  border-radius: 16px !important;
}

/* 斜体文本效果 */
em,
i {
  display: inline-block;
  box-sizing: border-box;
  background: rgba(247, 247, 247, 0.8) !important;
  border-radius: 8px;
  font-style: normal;
  color: rgba(50, 50, 50, 0.9);
}

q {
  color: rgba(0, 0, 0, 0.9);
  padding: 2px 6px;
  border-radius: 4px;
  border-bottom: 1px dotted rgba(200, 200, 200, 0.8);
  font-style: italic;
}

/* 强制覆盖标签页按钮的所有背景相关样式 */
.inline-drawer-header,
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4,
.standoutHeader {
  background-image: none !important;
  background: #1AAD19 !important;
  color: #f1f1f1 !important;
  border-image: none !important;
  box-shadow: none !important;
  -webkit-box-shadow: none !important;

}

.inline-drawer-header {
  border-radius: 10px;
  padding: 5px 10px;
  box-sizing: border-box;
  margin: 10px 0px;
}

.menu_button_icon,
.right_menu_button {}

.right_menu_button .fa-fw {
  margin-right: 10px !important;
}

/* 提示文本 */
.toggle-description {
  color: #EA6853 !important;
}

/* 复选框和单选框背景 */
input[type="checkbox"],
input[type="radio"] {
  background-color: rgba(255, 255, 255, 0.95) !important;
  border-color: rgba(220, 220, 220, 0.8) !important;
}

/* 复选框和单选框选中状态 */
input[type="checkbox"]:checked,
input[type="radio"]:checked {
  background-color: #1AAD19 !important;
  border-color: rgba(180, 180, 180, 0.9) !important;
}

/* 下拉选择框样式 */
select {
  background-color: rgba(255, 255, 255, 0.95) !important;
  border-color: rgba(220, 220, 220, 0.8) !important;
  color: rgba(50, 50, 50, 0.9) !important;
}

/* 输入框样式 */
input[type="text"],
input[type="number"],
textarea {
  background-color: rgba(255, 255, 255, 0.95) !important;
  border-color: rgba(220, 220, 220, 0.8) !important;
  color: rgba(50, 50, 50, 0.9) !important;
}

/* 标签和文本样式 */
label,
.checkbox_label {
  color: rgba(50, 50, 50, 0.9) !important;
}

/* 可选菜单项样式 */
.menu_item,
.dropdown-item {
  background-color: rgba(255, 255, 255, 0.95) !important;
  color: rgba(50, 50, 50, 0.9) !important;
}

/* 滑块样式 */
input[type="range"] {
  background-color: rgba(255, 255, 255, 0.95) !important;
}

input[type="range"]::-webkit-slider-thumb {
  background-color: rgba(200, 200, 200, 0.9) !important;
}

.neo-range-input {
  margin-top: 6px;
}

/* 代码块 */
pre {
  background: rgba(247, 247, 247, 0.95) !important;
  border: 2px dashed rgba(200, 200, 200, 0.8) !important;
  border-radius: 16px !important;
  padding: 12px !important;
  margin: 10px 0 !important;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05) !important;
}

/* 代码文本颜色 */
pre code {
  color: rgba(50, 50, 50, 0.9) !important;
}

.hljs,
code.hljs,
pre .hljs {
  background: transparent !important;
  background-color: transparent !important;
}

/* 确保代码高亮的背景也是透明的 */
.markdown-body pre,
.markdown-body code,
code[class*="language-"],
pre[class*="language-"] {
  background: transparent !important;
  background-color: transparent !important;
}

.mes .mesAvatarWrapper {
  margin: 6px;
}
.mes[is_user="true"]{
  display: flex;
  flex-direction: row-reverse;
}
.mes[is_user="true"] .mesAvatarWrapper {
  margin: 6px;
}

/* 消息气泡悬停特效 */
.mes_block:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 16px rgba(0, 0, 0, 0.1) !important;
  transition: all 0.3s ease;
}

