/* 输入框和选择框 */
input,
select,
textarea,
.text_pole,
.search_field {
  background-color: rgba(255, 255, 255, 0.95) !important;
  color: #4D4D4D !important;
  border: 1px solid rgba(220, 220, 220, 0.7) !important;
  border-radius: 8px !important;
  box-sizing: border-box !important;
}

.mes_block {
  padding: 10px;
  /* 增加消息气泡左侧的间距 */
}


/* 头像悬停效果 */
.mes .avatar img:hover {
  transform: scale(1.05);
  /* 略微放大 */
  transition: all 0.3s ease;
  border-style: dotted !important;
  /* 悬停时变为点状边框 */
  box-shadow: 0 0 12px rgba(180, 180, 180, 0.6) !important;
  /* 增强发光效果 */
}
